import re

filepath = "pythia_years_1000_3000_tokenized.txt"

def parse_years(file_path):
    years_past = []
    years_future = []

    for line in open(file_path, "r"):
        # Match: Year  [token_ids]  ['token1','token2']  #Tokens  NextToken
        m = re.match(r"^\s*(\d{4})\s+\[.*?\]\s+\[(.*?)\]\s+\d+\s+(\w+)", line)
        if m:
            year = int(m.group(1))
            tokens = [t.strip(" '") for t in m.group(2).split(',')]
            next_token = m.group(3)

            if len(tokens) == 2 and 1000 <= year <= 3000:
                if year < 2020 and next_token in ["was", "were"]:
                    years_past.append((year, tokens))
                elif year > 2020 and next_token in ["is", "are", "will"]:
                    years_future.append((year, tokens))

    return years_past, years_future

# Example usage
past, future = parse_years(filepath)

# Separate years and tokens for past
past_years = [y for y, _ in past]
past_tokens = [t for _, t in past]

# Separate years and tokens for future
future_years = [y for y, _ in future]
future_tokens = [t for _, t in future]

print("num past years:", len(past_years))
print("Past years:", past_years)
print("Past tokens:", past_tokens)
print("num future years:", len(future_years))
print("Future years:", future_years)
print("Future tokens:", future_tokens)







